mapper.select:
SELECT r.ID AS ID_RECETA, r.TITULO AS TITULO_RECETA, r.IMAGEN AS IMAGEN_RECETA, r.INSTRUCCIONES AS INSTRUCCIONES_RECETA, r.FECHA_CREACION AS FECHA_CREACION_RECETA, r.TIEMPO AS TIEMPO_RECETA, r.COMENSALES AS COMENSALES_RECETA, r.DIFICULTAD AS DIFICULTAD_RECETA, r.PUBLICADA AS PUBLICADA_RECETA, (SELECT COUNT(*) FROM LIKE_RECETA WHERE RECETA = R.ID) AS LIKES_RECETA, u.ID AS ID_USUARIO, u.NOMBRE AS NOMBRE_USUARIO, u.APELLIDO1 AS APELLIDO1_USUARIO, u.APELLIDO2 AS APELLIDO2_USUARIO, tr.ID AS ID_TIPO_RECETA, tr.NOMBRE AS NOMBRE_TIPO_RECETA FROM RECETAS r LEFT JOIN USUARIOS u ON u.ID = r.USUARIO LEFT JOIN TIPO_RECETA tr ON tr.ID = r.TIPO LEFT JOIN INGREDIENTE_RECETA ir ON ir.RECETA = r.ID WHERE R.PUBLICADA = 1 AND (#{tipoReceta} IS NULL OR tr.ID = #{tipoReceta}) AND (#{idUsuario} IS NULL OR u.ID = #{idUsuario}) AND (#{dificultad} IS NULL OR r.DIFICULTAD = #{dificultad}) AND (#{comensales} IS NULL OR r.COMENSALES = #{comensales}) AND (#{tiempo} IS NULL OR #{tiempo} >= r.TIEMPO)
AND R.TITULO LIKE #{titulo}
AND INGREDIENTE IN
#{element}
GROUP BY R.ID
HAVING COUNT(*) = #{size}

mapper.update:
    UPDATE RECETAS SET 
		PUBLICADA = 1
	WHERE ID = #{idReceta}

    UPDATE RECETAS SET 
		PUBLICADA = 0
	WHERE ID=#{idReceta}

mapper.select:
SELECT RECETA
FROM LIKE_RECETA
WHERE USUARIO = #{idUsuario}
